import React, { Component } from "react";


class Homepage3 extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
    };
  }

  componentDidMount() {
    //take random 10 result
    fetch("https://wknd-take-home-challenge-api.herokuapp.com/testimonial")
      .then((res) => res.json())
      // .then(res => console.log(res))
      .then((res) =>
        res.map((data) => ({
          //mapping data sesuai kebutuhan
          id: `${data.id}`,
          testimony: `${data.testimony}`,
          by: `${data.by}`,
        }))
      )
      .then((items) =>
        this.setState({
          items,
          // isLoaded: false
        })
      )
      .catch((error) => console.log("parsing failed", error));
  }

  render() {
    const { items } = this.state;
    console.log(items);
    return (
      <div>
        <div>
          {items.length > 0
            ? items.map((item) => {
                const { id, testimony, by } = item;
                return (
                  <div key={id} className="testimonial-card">
                    <div className="blukicks-card">
                      <h3>{item.by}</h3>
                      <p>{testimony}</p>
                    </div>
                    <div className="angelus-card">
                      <h3>{by}</h3>
                      <p>{testimony}</p>
                    </div>
                    <div className="soyoung-card">
                      <h3>{by}</h3>
                      <p>{testimony}</p>
                    </div>
                  </div>
                );
              })
            : null}
        </div>
      </div>
    );
  }
}

export default Homepage3;
