    console.log(items);
    return (
      <div>
        <div>
          {/* {items.length > 0
            ? items.map((item) => { */}
                const { id, testimony, by } = item;
                return (
                  <div key={id} className="testimonial-card">
                    <div className="blukicks-card">
                      <h3>{by}</h3>
                      <p>{testimony}</p>
                    </div>
                  </div>
                );
              })
            : null}
        </div>
      </div>
    );
  }
}

export default Homepage3;
