import React, { Fragment,useState } from "react";
import thumbnail from "../Assetss/smile.png";
import avatar from "../Assetss/astro.png";
import astronot from "../Assetss/astronot.png";
import oval from "../Assetss/oval.svg";
import gambarcard1 from "../Assetss/gambar-card1.png";
import gambarcard2 from "../Assetss/gambar-card2.png";
import gambarcard from "../Assetss/gambar-card.png";
import astronott from "../Assetss/astronott.png";
// import ovalicon from "../Assetss/oval-icon.svg";
import "../Assetss/icons/css/all.min.css";
import ScrollMenu from 'react-horizontal-scrolling-menu';



const list = [
  { name: 'item1' },
  { name: 'item2' },
  { name: 'item3' },
  { name: 'item4' },
  { name: 'item5' },
  { name: 'item6' },
  { name: 'item7' },
  { name: 'item8' },
  { name: 'item9' }
];


// One item component
// selected prop will be passed
const MenuItem = ({text, selected}) => {
  return <div
    className={`menu-item ${selected ? 'active' : ''}`}
    >{text}</div>;
};

// All items component
// Important! add unique key
export const Menu = (list, selected) =>
  list.map(el => {
    const {name} = el;

    return <MenuItem text={name} key={name} selected={selected} />;
  });


const Arrow = ({ text, className }) => {
  return (
    <div
      className={className}
    >{text}</div>
  );
};


const ArrowLeft = Arrow({ text: '<', className: 'arrow-prev' });
const ArrowRight = Arrow({ text: '>', className: 'arrow-next' });

const selected = 'item1';

const Homepage = () => {
  const  [selected, setSelected]= useState(null)
  const onSelect = key => {
   setSelected(key)
  }
  const menu = Menu(list, selected);

  return (
    <>
      <div className="header">
        <img src={thumbnail} className="thumbnail" />
        <div>
          <p>Good Morning</p>
          <strong className="fellas">Fellas</strong>
        </div>
      </div>
      {/* Homepage 1 */}
      <div className="background-1">
        <h1 className="weekend">Weekend From Home</h1>
        <h4>Stay active with a little workout</h4>
        <img src={avatar} className="avatar-1" />
      </div>
      {/* Homepage 2  */}
      <div className="background-2">
        <img src={astronot} className="astronot" />
        <div className="text">
          <p>
            <span id="definition">Definition;</span> a practice or exercise to
            test or improve one's fitness for athletic competition, ability, or
            performance to exhaust (something, such as a mine) by working to
            devise, arrange, or achieve by resolving difficulties.
            Merriam-Webster.com Dictionary.
          </p>
          <span>-weekend team</span>
          <img src={oval} className="oval" />
          <h2>Testimonial</h2>
        </div>
      </div>
      {/* Homepage 3 testimonial card */}
      <ScrollMenu
          data={menu}
          arrowLeft={ArrowLeft}
          arrowRight={ArrowRight}
          selected={selected}
          onSelect={onSelect}
        />
      <div className="homepagess">
        <div className="homepage3">
          <div className="testimonial-card">

            <div className="blukicks-card">
              <h3>Blu Kicks</h3>
              <p>
                Places where you can leverage tools and software to free up time
                to focus on growing the business.
              </p>
            </div>
            <div className="angelus-card">
              <h3>Angelus</h3>
              <p>
                All those apps took me months to get running. Now the site
                practically runs itself!
              </p>
            </div>
            <div className="soyoung-card">
              <h3>SoYoung</h3>
              <p>
                Unless you have a truly unique product, it will be very hard to
                differentiate and gain brand traction
              </p>
            </div>
          </div>
          <div className="pov">
            <h1>POV</h1>
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
              eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
              enim ad minim veniam, quis nostrud ullamco laboris nisi ea commodo
              consequat. Duis aute irure dolor in reprehenderit in voluptate
              velit esse cillum dolore eu fugiat nulla pariatur.{" "}
            </p>
          </div>
          <div className="resources">
            <h1>Resources</h1>
            <p>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
              eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
              enim ad minim veniam, quis nostrud ullamco laboris nisi ea commodo
              consequat. Duis aute irure dolor in reprehenderit in voluptate
              velit esse cillum dolore eu fugiat nulla pariatur.{" "}
            </p>
          </div>

          {/* Homepage 4 help and tips*/}
          <div className="card-helpntips">
            <h1>Help and Tips</h1>
            <div className="cardhelpandtips">
              <div className="card-img">
                <img src={gambarcard1} className="gambar-card1" />
                <p>
                  Start quickly with simple steps{" "}
                  <i className="fas fa-arrow-circle-right fa-2x"></i>
                </p>
              </div>

              <div className="card-img-2">
                <img src={gambarcard2} className="gambar-card2" />
                <p>
                  Start quickly with simple steps{" "}
                  <i className="fas fa-arrow-circle-right fa-2x"></i>
                </p>
              </div>

              <div className="card-img-3">
                <img src={gambarcard} className="gambarcard" />
                <p>
                  Start quickly with simple steps{" "}
                  <i className="fas fa-arrow-circle-right fa-2x"></i>
                </p>
              </div>
            </div>
          </div>
          <div className="youreset">
            {/* <img src={astronott} className="astronott" /> */}
            <div className="yoursetwrap">
              <h1>You’re all set.</h1>
              <p>
                The wise man therefore always holds in these matters to this
                principle of selection.
              </p>
            </div>
          </div>
        </div>
      </div>
      <div className="footer">
        <p className="wknd2020">wknd@2020</p>
        <button className="button">alpha version</button>
      </div>
    </>
  );
};

export default Homepage;
