import React, { Component } from "react";



class Homepage4 extends Component {
    constructor(props){
        super(props);
        this.state =  {
            items: []
        }
    }

    componentDidMount(){
        //take random 10 result
        fetch("https://wknd-take-home-challenge-api.herokuapp.com/help-tips")
            .then(res => res.json())
            // .then(res => console.log(res))
            .then(res => res.map(data => (
                {
                    //mapping data sesuai kebutuhan
                    id: `${data.id}`,
                    image: `${data.image}`,
                    tittle: `${data.tittle}`,

                }
            )))
            .then(items => this.setState({
                items
                // isLoaded: false
            }))
            .catch(error => console.log('parsing failed', error))
    }

    render(){
        const {items } = this.state
        console.log(items)
        return(
           <div>
               <div>
          {items.length > 0
            ? items.map((item) => {
                const { id, image, tittle } = item;
                return (
                   <div className="card-helpntips">
            <h1>Help and Tips</h1>
            <div className="cardhelpandtips">
              <div className="card-img">
                <img src={image} className="gambar-card1" />
                <p>
                 {tittle}
                  <i className="fas fa-arrow-circle-right fa-2x"></i>
                </p>
              </div>

              <div className="card-img-2">
                <img src={image} className="gambar-card2" />
                <p>
                    {tittle}
                  <i className="fas fa-arrow-circle-right fa-2x"></i>
                </p>
              </div>

              <div className="card-img-3">
                <img src={image} className="gambarcard" />
                <p>
                  {tittle}
                  <i className="fas fa-arrow-circle-right fa-2x"></i>
                </p>
              </div>
            </div>
          </div> 
                );
              })
            : null}
        </div>
            </div>
        )
    }
}

export default Homepage4;
